mapper.select:
SELECT 

FROM pur_planmain a

WHERE a.id = #{id}

SELECT 

FROM pur_planmain a

WHERE 1=1

		AND a.bill_num =#{billNum} 


		AND a.id = #{id}

SELECT 

FROM pur_planmain a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.bill_num LIKE 
			'%'||#{billNum}||'%'
			'%'+#{billNum}+'%'
			concat('%',#{billNum},'%')


		AND a.plan_date BETWEEN #{beginPlanDate} AND #{endPlanDate}


		AND a.make_date BETWEEN #{beginMakeDate} AND #{endMakeDate}


		AND a.make_empid = #{makeEmpid.id}


		AND a.bill_state_flag = #{billStateFlag}


		AND a.bill_type = #{billType}


		AND a.make_empname LIKE 
			'%'||#{makeEmpname}||'%'
			'%'+#{makeEmpname}+'%'
			concat('%',#{makeEmpname},'%')


		AND a.plan_type_code = #{planTypeCode.id}


		AND a.plan_type_name LIKE 
			'%'||#{planTypeName}||'%'
			'%'+#{planTypeName}+'%'
			concat('%',#{planTypeName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pur_planmain a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pur_planmain SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
