mapper.select:
SELECT 

FROM mdm_item a

WHERE a.id = #{id}

SELECT
FROM mdm_item a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.code LIKE '%'||#{code}||'%' '%'+#{code}+'%' concat('%',#{code},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND (b.id = #{classCode.id} OR b.parent_ids LIKE '%,'||#{classCode.id}||',%') CONCAT('%,', #{classCode.id}, ',%')) '%'+#{classCode.id}+'%')
AND a.class_name = #{className}
AND a.spec_model LIKE '%'||#{specModel}||'%' '%'+#{specModel}+'%' concat('%',#{specModel},'%')
AND a.texture LIKE '%'||#{texture}||'%' '%'+#{texture}+'%' concat('%',#{texture},'%')
AND a.unit_code = #{unitCode.unitCode}
AND a.unit = #{unit}
AND a.system_sign LIKE '%'||#{systemSign}||'%' '%'+#{systemSign}+'%' concat('%',#{systemSign},'%')
AND a.is_key = #{isKey}
AND a.is_in_motor = #{isInMotor}
AND a.class_code LIKE #{classCode.classId}"%"
ORDER BY ${page.orderBy}
ORDER BY a.code ASC

SELECT
FROM mdm_item a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.code LIKE #{code}||'%' #{code}+'%' concat(#{code},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND (b.id = #{classCode.id} OR b.parent_ids LIKE #{classCode.id}||'%') #{classCode.id}+'%') concat(#{classCode.id},'%'))
AND a.class_name = #{className}
AND a.spec_model LIKE '%'||#{specModel}||'%' '%'+#{specModel}+'%' concat('%',#{specModel},'%')
AND a.texture LIKE '%'||#{texture}||'%' '%'+#{texture}+'%' concat('%',#{texture},'%')
AND a.unit_code = #{unitCode.unitCode}
AND a.unit = #{unit}
AND a.system_sign LIKE '%'||#{systemSign}||'%' '%'+#{systemSign}+'%' concat('%',#{systemSign},'%')
AND a.is_key = #{isKey}
AND a.is_in_motor = #{isInMotor}
AND a.class_code LIKE #{classCode.classId}"%"
ORDER BY ${page.orderBy}
ORDER BY a.code ASC

SELECT 

FROM mdm_item a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM mdm_item


		WHERE id = #{id}


		WHERE class_code = #{classCode.classId}

UPDATE mdm_item SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE class_code = #{classCode.classId}
