mapper.select:
SELECT 

FROM sal_linkman a

WHERE a.id = #{id}

SELECT 

FROM sal_linkman a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.account_code LIKE 
			'%'||#{accountCode}||'%'
			'%'+#{accountCode}+'%'
			concat('%',#{accountCode},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM sal_linkman a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM sal_linkman


		WHERE id = #{id}


		WHERE account_code = #{accountCode}

UPDATE sal_linkman SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE account_code = #{accountCode}
