mapper.select:
SELECT 

FROM inv_billdetail a

WHERE a.id = #{id}

SELECT
FROM inv_billdetail a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.bill_num LIKE '%'||#{billNum.id}||'%' '%'+#{billNum.id}+'%' concat('%',#{billNum.id},'%')
AND a.serial_num LIKE '%'||#{serialNum}||'%' '%'+#{serialNum}+'%' concat('%',#{serialNum},'%')
AND a.bin_id = #{bin.id}
AND a.bin_name LIKE '%'||#{binName}||'%' '%'+#{binName}+'%' concat('%',#{binName},'%')
AND a.loc_id = #{loc.id}
AND a.loc_name LIKE '%'||#{locName}||'%' '%'+#{locName}+'%' concat('%',#{locName},'%')
AND a.item_code = #{item.id}
AND a.item_name LIKE '%'||#{itemName}||'%' '%'+#{itemName}+'%' concat('%',#{itemName},'%')
AND a.item_spec LIKE '%'||#{itemSpec}||'%' '%'+#{itemSpec}+'%' concat('%',#{itemSpec},'%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date ASC

SELECT 

FROM inv_billdetail a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM inv_billdetail


		WHERE id = #{id}


		WHERE bill_num = #{billNum.id}

UPDATE inv_billdetail SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE bill_num = #{billNum.id}
