mapper.select:
SELECT 

FROM inv_billmain a

WHERE a.id = #{id}

SELECT
FROM inv_billmain a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.create_by = #{userId}
AND a.bill_num LIKE '%'||#{billNum}||'%' '%'+#{billNum}+'%' concat('%',#{billNum},'%')
AND substring(a.bill_num from 1 for 3) like 'wgo'
AND a.bill_date BETWEEN #{beginBillDate} AND #{endBillDate}
AND a.dept_id = #{dept.code}
AND a.dept_name LIKE '%'||#{deptName}||'%' '%'+#{deptName}+'%' concat('%',#{deptName},'%')
AND a.io_type = #{io.ioType}
AND a.io_desc LIKE '%'||#{ioDesc}||'%' '%'+#{ioDesc}+'%' concat('%',#{ioDesc},'%')
AND a.ware_id = #{ware.wareID}
AND a.ware_name LIKE '%'||#{wareName}||'%' '%'+#{wareName}+'%' concat('%',#{wareName},'%')
AND a.bill_person = #{billPerson.no}
AND a.ware_empid = #{wareEmp.user.no}
AND a.ware_empname LIKE '%'||#{wareEmpname}||'%' '%'+#{wareEmpname}+'%' concat('%',#{wareEmpname},'%')
AND a.use_id = #{invuse.useId}
AND a.use_name LIKE '%'||#{useName}||'%' '%'+#{useName}+'%' concat('%',#{useName},'%')
AND a.bill_flag = #{billFlag}
AND a.rec_empid = #{recEmp.no}
AND a.rec_empname LIKE '%'||#{recEmpname}||'%' '%'+#{recEmpname}+'%' concat('%',#{recEmpname},'%')
AND a.rec_date BETWEEN #{beginRecDate} AND #{endRecDate}
AND a.bill_empid = #{billEmp.no}
AND a.bill_empname LIKE '%'||#{billEmpname}||'%' '%'+#{billEmpname}+'%' concat('%',#{billEmpname},'%')
AND a.period_id LIKE '%'||#{period.periodId}||'%' '%'+#{period.periodId}+'%' concat('%',#{period.periodId},'%')
AND a.bill_source = #{billSource}
ORDER BY ${page.orderBy}
ORDER BY a.bill_date DESC

SELECT 

FROM inv_billmain a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.bill_source desc, a.bill_date DESC

mapper.update:
UPDATE inv_billmain SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
