mapper.select:
SELECT 

FROM pur_linkman a

WHERE a.id = #{id}

SELECT
FROM pur_linkman a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.link_code LIKE '%'||#{linkCode}||'%' '%'+#{linkCode}+'%' concat('%',#{linkCode},'%')
AND a.link_name LIKE '%'||#{linkName}||'%' '%'+#{linkName}+'%' concat('%',#{linkName},'%')
AND a.mdm_account_id = #{Puraccount.id}
ORDER BY ${page.orderBy}
ORDER BY a.create_date ASC

SELECT 

FROM pur_linkman a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM pur_linkman


		WHERE id = #{id}


		WHERE mdm_account_id = #{Puraccount.id}

UPDATE pur_linkman SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE mdm_account_id = #{Puraccount.id}
