mapper.select:
SELECT 

FROM mdm_account a

WHERE a.id = #{id}

SELECT 

FROM mdm_account a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.account_code = #{accountCode}


		AND a.account_name = #{accountName}


		AND a.area_code = #{areaCode}


		AND a.area_name = #{areaName}


		AND a.account_prop = #{accountProp}


		AND a.sub_type_code = #{subTypeCode}


		AND a.sub_type_name = #{subTypeName}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mdm_account a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mdm_account SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
